import React, { Component } from "react";
import "./App.css";
import Char from "./Char";
import Validation from "./Validation";

class App extends Component {
  state = {
    textLength: 0,
    txtArray: [],
    text:""
  };

  textOperations = (event) => {

    let length = 0;
    const valueTxt = event.target.value;

    length = valueTxt.length;

    const valueArr = valueTxt.split('');

    this.setState({
      textLength: length,
      txtArray: valueArr,
      text:valueTxt
    });
  }

  deleteOperations = (event, index) => {

  }

  render() {
    let charList = this.state.txtArray.map((ch, index) => {
          return <Char letter={ch} key={index} />
        });

    return (
      <div className="App">
        <div>
          <label>
            Please input a text :
            <input
              type="text"
              onChange={(event) => this.textOperations(event)}
            />
          </label>
        </div>
        <Validation txtLength={this.state.textLength} />
        {charList}
      </div>
    );
  }
}

export default App;
