import { useState } from "react";
import "./App.css";
import Validation from "./Validation";

function App() {

  const [textState, changeTextState] = useState({
    textLength: 0
  });

  const findLengthOfText = (event) => {
    let length = 0;
    const valueTxt = event.target.value;

    length = valueTxt.length;

    console.log("Length : "+length);

    changeTextState({
      textLength:length
    });
  };

  return (
    <div className="App">
      <div>
        <label>Please input a text : 
          <input type="text" 
            onChange={(event) => findLengthOfText(event)}
          />
        </label>
      </div>
      <Validation txtLength={textState.textLength} />
    </div>
  );
}

export default App;
