import { useState } from "react";
import "./App.css";

function App() {

  const [textState, changeTextState] = useState({
    textLength: 0
  });

  const findLengthOfText = (event) => {
    let length = 0;
    const valueTxt = event.target.value;

    length = valueTxt.length;

    console.log("Length : "+length);

    changeTextState({
      textLength:length
    });
  };

  return (
    <div className="App">
      <div>
        <label>Please input a text : </label>
        <input
          type="textarea"
          id="myTextArea"
          onChange={(event) => findLengthOfText(event)}
        />
      </div>
      <div>
        <label>The length of your text is </label>
        <p>{textState.textLength}</p>
      </div>
    </div>
  );
}

export default App;
