import "./App.css";

function App() {

  const findLengthOfText = (event) => {
    let length = 0;
    const valueTxt = event.target.value;
    console.log("Target value : "+valueTxt);
  };

  return (
    <div className="App">
      <label>Please input a text :</label>
      <input
        type="textarea"
        id="myTextArea"
        onChange={(event) => findLengthOfText(event)}
      />
    </div>
  );
}

export default App;
